package com.java;

public class App4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int n=4;
		int num=1;
	    for(int i=1; i<=n; i++){
	    	for(int j=1; j<=i; j++) {
	    		System.out.print(num+" ");
	    		num++;
	    	}
	    	System.out.println(" ");
	        }
	}

}
